import React from 'react'

function Popup({ selected, closePopup }) {
	console.log(selected);

	const year = selected.end_date;
  var desc = '';
	if(selected.synopsis){
    desc = selected.synopsis;
  }
  else{
     desc = "No Description available";
  }
  var trailer = '<h4>No trailer available</h4>';
  if(selected.trailer.embed_url){


   trailer = ' <iframe height="300px" width="100%" src={selected.trailer.embed_url}></iframe>' ;
  }

	return (
		<section className="popup">





<main>
  <div class="container">
    <div class="grid second-nav">
      <div class="column-xs-12">
        <nav>
          <ol class="breadcrumb-list">
            <li class="breadcrumb-item">{selected.rating}</li>
			<li class="breadcrumb-item">{selected.type} </li>
				{selected.genres.map((genre) => (
				<li class="breadcrumb-item"> {genre.name}</li>
			))}  
			<li class="breadcrumb-item">{selected.year} </li>


          </ol>
        </nav>
      </div>
    </div>
    <div class="grid product">
      <div class="column-xs-12 column-md-5">
        <div class="product-gallery">
          <div class="product-image">
            <img class="active" src={selected.images.jpg.large_image_url}/>
          </div>

        </div>
      </div>
      <div class="column-xs-12 column-md-7">
        <h1 className='anime-title'>{ selected.title } </h1>

        <div class="description">
			<p>{desc}</p>
          </div>

      </div>
    </div>
    <div class="grid related-products">
      <div class="column-xs-12">
        <h3>Trailer</h3>
      </div>
      <div class="column-xs-12 column-md-8">
	  {trailer}

      </div>
	  <div class="column-xs-12">
	  <button className="close" onClick={closePopup}>Close</button>
      </div>


    </div>

  </div>

</main>

		</section>

	)
}

export default Popup
